import { Component } from '@angular/core';
import { CalculatorApiService } from '../services/calculator-api.service';

@Component({
  selector: 'app-calculator',
  templateUrl: './calculator.component.html',
  styleUrls: ['./calculator.component.scss']
})
export class CalculatorComponent {
  result: number = 0;
  num1: number = 0;
  currentInput: number = 0;
  operation: string = '';

  constructor(private calculatorApiService: CalculatorApiService) { }

  onCalculate() {
    if (this.num1 && this.currentInput && this.operation) {
      this.calculatorApiService.calculate(this.num1, this.currentInput, this.operation).subscribe(
        (response) => {
          this.result = response;
          this.num1 = 0;
          this.currentInput = 0;
          this.operation = '';
        },
        (error) => {
          console.error('Error:', error);
        }
      );
    }
  }

  handleButtonClick(value: string) {
    if (!isNaN(Number(value))) {
      // If it's a number
      this.currentInput = this.currentInput ? this.currentInput * 10 + Number(value) : Number(value);
    } else {
      switch (value) {
        case 'add':
        case 'subtract':
        case 'multiply':
        case 'divide':
          this.operation = value;
          this.num1 = this.currentInput;
          this.currentInput = 0;
          break;
        case 'equals':
          this.onCalculate();
          break;
        default:
          break;
      }
    }
  }

}
<div class="calculator">
  <h1>Simple Calculator</h1>
  <app-screen [result]="result"></app-screen>
  <app-buttons (buttonClickedEvent)="handleButtonClick($event)"></app-buttons>
</div>
