import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-buttons',
  templateUrl: './buttons.component.html',
  styleUrls: ['./buttons.component.scss']
})
export class ButtonsComponent {
  @Output() buttonClickedEvent = new EventEmitter<string>();

  buttons = [
    [
      { label: '7', value: '7' },
      { label: '4', value: '4' },
      { label: '1', value: '1' },
      { label: '0', value: '0' },
    ],
    [
      { label: '8', value: '8' },
      { label: '5', value: '5' },
      { label: '2', value: '2' },
      { label: '.', value: '.' },
    ],
    [
      { label: '9', value: '9' },
      { label: '6', value: '6' },
      { label: '3', value: '3' },
      { label: '=', value: 'equals', class: 'equals' },
    ],
    [
      { label: '÷', value: 'divide', class: 'operation' },
      { label: '×', value: 'multiply', class: 'operation' },
      { label: '−', value: 'subtract', class: 'operation' },
      { label: '+', value: 'add', class: 'operation' },
    ],
    [
      { label: 'C', value: 'clear' },
    ],
  ];

  onButtonClick(value: string) {
    this.buttonClickedEvent.emit(value);
  }
}
<div class="buttons">
  <div class="row" *ngFor="let row of buttons">
    <button
      class="btn"
      [ngClass]="button.class"
      *ngFor="let button of row"
      (click)="onButtonClick(button.value)"
    >
      {{ button.label }}
    </button>
  </div>
</div>
