import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-buttons',
  templateUrl: './buttons.component.html',
  styleUrls: ['./buttons.component.scss']
})
export class ButtonsComponent {
  @Output() buttonClickedEvent = new EventEmitter<string>();

  buttons = [
    [{ label: '7' }, { label: '8' }, { label: '9' }, { label: '÷', value: 'divide' }],
    [{ label: '4' }, { label: '5' }, { label: '6' }, { label: '×', value: 'multiply' }],
    [{ label: '1' }, { label: '2' }, { label: '3' }, { label: '−', value: 'subtract' }],
    [{ label: '0' }, { label: '.' }, { label: '=', value: 'equals' }, { label: '+', value: 'add' }]
  ];

  buttonClicked(button: { label: string; value?: string }) {
    this.buttonClickedEvent.emit(button.value || button.label);
  }
}
<div class="buttons">
  <div class="row" *ngFor="let row of buttons">
    <button class="btn" *ngFor="let button of row" (click)="buttonClicked(button)">{{ button.label }}</button>
  </div>
</div>
