import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-buttons',
  templateUrl: './buttons.component.html',
  styleUrls: ['./buttons.component.scss']
})
export class ButtonsComponent {
  @Output() buttonClickedEvent = new EventEmitter<string>();

  buttons = [
    [
      { label: '7', value: '7' },
      { label: '8', value: '8' },
      { label: '9', value: '9' },
      { label: '÷', value: 'divide', class: 'operator' },
    ],
    [
      { label: '4', value: '4' },
      { label: '5', value: '5' },
      { label: '6', value: '6' },
      { label: '×', value: 'multiply', class: 'operator' },
    ],
    [
      { label: '1', value: '1' },
      { label: '2', value: '2' },
      { label: '3', value: '3' },
      { label: '-', value: 'subtract', class: 'operator' },
    ],
    [
      { label: '0', value: '0' },
      { label: '.', value: '.' },
      { label: '=', value: 'equals', class: 'operator' },
      { label: '+', value: 'add', class: 'operator' },
    ],
  ];

  onButtonClick(value: string) {
    this.buttonClickedEvent.emit(value);
  }
}
<div class="buttons">
  <div class="row" *ngFor="let row of buttons; let i = index">
    <button
      class="btn"
      [ngClass]="button.class"
      *ngFor="let button of row"
      (click)="onButtonClick(button.value)"
    >
      {{ button.label }}
    </button>
    <button *ngIf="i === 0" class="btn" (click)="onButtonClick('clear')">C</button>
  </div>
</div>
