import { Component } from '@angular/core';
import { CalculatorApiService } from '../services/calculator-api.service';

@Component({
  selector: 'app-calculator',
  templateUrl: './calculator.component.html',
  styleUrls: ['./calculator.component.scss']
})
export class CalculatorComponent {
  result: number = 0;
  num1: number = 0;

  constructor(private calculatorApiService: CalculatorApiService) { }

  onCalculate(event: {currentInput: number, operation: string}) {
    if (!this.num1) {
      this.num1 = event.currentInput;
    } else {
      this.calculatorApiService.calculate(this.num1, event.currentInput, event.operation).subscribe(
        (response) => {
          this.result = response;
          this.num1 = 0;
        },
        (error) => {
          console.error('Error:', error);
        }
      );
    }
  }
}
