import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-buttons',
  templateUrl: './buttons.component.html',
  styleUrls: ['./buttons.component.scss']
})
export class ButtonsComponent {
  @Output() calculate = new EventEmitter<{currentInput: number, operation: string}>();

  currentInput: string = '';
  operation: string = '';

  appendToInput(num: number) {
    this.currentInput += num.toString();
  }

  onCalculate() {
    this.calculate.emit({ currentInput: parseFloat(this.currentInput), operation: this.operation });
    this.currentInput = '';
  }
}
